import React from 'react';
import { motion } from 'framer-motion';

interface AdaptationStrategiesProps {
  jobId: string;
}

const AdaptationStrategies: React.FC<AdaptationStrategiesProps> = ({ jobId }) => {
  // 根据职业ID获取适应策略
  const getStrategies = (id: string): string[] => {
    switch (id) {
      case 'programmer':
        return [
          '专注于AI系统设计和架构能力',
          '发展AI提示工程和系统集成技能',
          '培养创造性解决问题和团队协作能力',
          '学习新兴技术如量子计算、区块链等'
        ];
      case 'doctor':
        return [
          '发展AI辅助诊断工具的使用能力',
          '专注于医患沟通和同理心等人际技能',
          '学习医疗AI系统的解释和验证',
          '发展跨学科合作能力'
        ];
      // 为其他职业添加策略...
      default:
        return [
          '持续学习和适应新技术',
          '发展创造力和批判性思维',
          '培养跨学科能力和团队协作',
          '关注人际交往和情感智能'
        ];
    }
  };

  const strategies = getStrategies(jobId);

  return (
    <motion.div 
      className="adaptation-strategies"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8, delay: 0.6 }}
    >
      <h3>发展建议与适应策略</h3>
      <ul className="strategies-list">
        {strategies.map((strategy, index) => (
          <motion.li 
            key={index}
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.1 * index + 0.7 }}
          >
            {strategy}
          </motion.li>
        ))}
      </ul>
    </motion.div>
  );
};

export default AdaptationStrategies;
