import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { jobsData } from '../data/jobsData';
import { Job } from '../types/types';
import JobSubmissionForm from './JobSubmissionForm';

interface JobSearchProps {
  onSelectJob: (job: Job) => void;
}

const JobSearch: React.FC<JobSearchProps> = ({ onSelectJob }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredJobs, setFilteredJobs] = useState<Job[]>([]);
  const [showResults, setShowResults] = useState(false);
  const [showSubmissionForm, setShowSubmissionForm] = useState(false);
  const searchRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredJobs([]);
      setShowResults(false);
      return;
    }

    const results = jobsData.filter(job =>
      job.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredJobs(results);
    setShowResults(true);
  }, [searchTerm]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (searchRef.current && !searchRef.current.contains(event.target as Node)) {
        setShowResults(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleSelectJob = (job: Job) => {
    onSelectJob(job);
    setSearchTerm(job.title);
    setShowResults(false);
  };

  const handleSubmitClick = () => {
    setShowSubmissionForm(true);
    setShowResults(false);
  };

  return (
    <div className="job-search" ref={searchRef}>
      <h2>Êü•ËØ¢ÊÇ®ÁöÑËÅå‰∏ö</h2>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="ËæìÂÖ•ËÅå‰∏öÂêçÁß∞..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onFocus={() => searchTerm.trim() !== '' && setShowResults(true)}
        />
        <div className="search-icon">üîç</div>

        <AnimatePresence>
          {showResults && (
            <motion.div
              className="search-results"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <div
                    key={job.id}
                    className="search-result-item"
                    onClick={() => handleSelectJob(job)}
                  >
                    {job.title}
                  </div>
                ))
              ) : (
                <div className="no-results">
                  <p>Êú™ÊâæÂà∞Áõ∏ÂÖ≥ËÅå‰∏ö</p>
                  <button 
                    className="submit-job-button"
                    onClick={handleSubmitClick}
                  >
                    Êèê‰∫§Êñ∞ËÅå‰∏ö
                  </button>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>

      <AnimatePresence>
        {showSubmissionForm && (
          <motion.div 
            className="modal-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowSubmissionForm(false)}
          >
            <div 
              className="modal-content"
              onClick={(e) => e.stopPropagation()}
            >
              <JobSubmissionForm onClose={() => setShowSubmissionForm(false)} />
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default JobSearch;
