import React, { useEffect, useState, useCallback, useMemo, useRef } from 'react';
import { motion } from 'framer-motion';
import * as THREE from 'three';
import { Canvas, useFrame } from '@react-three/fiber';

interface ProbabilityMeterProps {
  probability: number; // 0-100
}

interface Particle {
  id: number;
  angle: number;
  distance: number;
  size: number;
  speed: number;
  pulse: number;
}

// 3D环形进度条组件
const RingProgress: React.FC<{ probability: number }> = ({ probability }) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const materialRef = useRef<THREE.ShaderMaterial>(null);

  // 创建自定义着色器材质
  const shaderData = useMemo(() => {
    return {
      uniforms: {
        time: { value: 0 },
        progress: { value: probability / 100 },
        color: { value: new THREE.Color(getColorFromProbability(probability)) }
      },
      vertexShader: `
        varying vec2 vUv;
        void main() {
          vUv = uv;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragmentShader: `
        uniform float time;
        uniform float progress;
        uniform vec3 color;
        varying vec2 vUv;

        void main() {
          // 计算当前点到圆心的距离
          vec2 center = vec2(0.5, 0.5);
          float dist = distance(vUv, center);

          // 创建环形
          float ring = smoothstep(0.3, 0.31, dist) * smoothstep(0.4, 0.39, dist);

          // 创建进度效果
          float angle = atan(vUv.y - 0.5, vUv.x - 0.5);
          angle = angle < 0.0 ? angle + 6.28318 : angle;
          float progressAngle = progress * 6.28318;
          float progressMask = step(angle, progressAngle);

          // 添加脉冲效果
          float pulse = 0.5 + 0.5 * sin(time * 3.0 + dist * 10.0);
          float glow = smoothstep(0.35, 0.36, dist) * smoothstep(0.38, 0.37, dist) * pulse;

          // 最终颜色
          vec3 ringColor = color * (0.8 + 0.2 * pulse);
          vec3 glowColor = color * 0.6;

          // 应用进度遮罩
          ringColor *= progressMask;
          glowColor *= progressMask;

          // 组合环形和辉光
          gl_FragColor = vec4(ringColor * ring + glowColor * glow, ring * progressMask + glow * 0.7 * progressMask);
        }
      `
    };
  }, [probability]);

  // 动画更新
  useFrame(({ clock }) => {
    if (materialRef.current) {
      materialRef.current.uniforms.time.value = clock.getElapsedTime();
    }
  });

  return (
    <mesh rotation={[0, 0, -Math.PI / 2]}>
      <planeGeometry args={[2, 2]} />
      <shaderMaterial 
        ref={materialRef}
        args={[shaderData]}
        transparent={true}
      />
    </mesh>
  );
};

// 获取基于概率的颜色
const getColorFromProbability = (probability: number): string => {
  if (probability < 30) return '#4CAF50'; // 绿色 - 低风险
  if (probability < 70) return '#FF9800'; // 橙色 - 中风险
  return '#F44336'; // 红色 - 高风险
};

// 粒子系统
const ParticleSystem: React.FC<{ probability: number }> = ({ probability }) => {
  const particlesRef = useRef<THREE.Points>(null);

  // 粒子数量和速度基于概率
  const particleCount = useMemo(() => Math.floor(probability * 1.5) + 20, [probability]);
  const particleSpeed = useMemo(() => 0.2 + (probability / 100) * 0.8, [probability]);

  // 创建粒子
  const [positions, colors] = useMemo(() => {
    const positions = new Float32Array(particleCount * 3);
    const colors = new Float32Array(particleCount * 3);
    const color = new THREE.Color(getColorFromProbability(probability));

    for (let i = 0; i < particleCount; i++) {
      // 随机位置，但集中在圆环附近
      const angle = Math.random() * Math.PI * 2;
      const radius = 0.35 + Math.random() * 0.1; // 环形区域

      const i3 = i * 3;
      positions[i3] = Math.cos(angle) * radius;
      positions[i3 + 1] = Math.sin(angle) * radius;
      positions[i3 + 2] = (Math.random() - 0.5) * 0.1;

      // 颜色
      colors[i3] = color.r;
      colors[i3 + 1] = color.g;
      colors[i3 + 2] = color.b;
    }

    return [positions, colors];
  }, [particleCount, probability]);

  // 动画更新
  useFrame(({ clock }) => {
    if (particlesRef.current) {
      const time = clock.getElapsedTime();
      const positions = particlesRef.current.geometry.attributes.position.array as Float32Array;

      for (let i = 0; i < particleCount; i++) {
        const i3 = i * 3;
        const x = positions[i3];
        const y = positions[i3 + 1];

        // 计算当前角度
        let angle = Math.atan2(y, x);
        const radius = Math.sqrt(x * x + y * y);

        // 更新角度
        angle += particleSpeed * 0.01 * (1 + Math.sin(time + i * 0.1));

        // 更新位置
        positions[i3] = Math.cos(angle) * radius;
        positions[i3 + 1] = Math.sin(angle) * radius;
      }

      particlesRef.current.geometry.attributes.position.needsUpdate = true;
    }
  });

  return (
    <points ref={particlesRef}>
      <bufferGeometry>
        <bufferAttribute
          attach="attributes-position"
          count={particleCount}
          array={positions}
          itemSize={3}
        />
        <bufferAttribute
          attach="attributes-color"
          count={particleCount}
          array={colors}
          itemSize={3}
        />
      </bufferGeometry>
      <pointsMaterial 
        size={0.03} 
        vertexColors 
        transparent 
        opacity={0.8}
        blending={THREE.AdditiveBlending}
      />
    </points>
  );
};

