import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import CountdownClock from './CountdownClock';
import ProbabilityMeter from './ProbabilityMeter';
import Timeline from './Timeline';
import JobIcon3D from './JobIcon3D';
import AdaptationStrategies from './AdaptationStrategies';
import { jobsData } from '../data/jobsData';

const JobDetails: React.FC = () => {
  const { t } = useLanguage();

  // 使用默认职业（程序员）
  const selectedJob = jobsData.find(job => job.title === "程序员") || jobsData[0];

  return (
    <motion.div 
      className="job-details"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h2>{t('jobDetails.title')}</h2>

      <div className="data-visualization">
        <div className="visualization-row">
          <CountdownClock 
            predictedYear={selectedJob.predictedYear} 
            automationProbability={selectedJob.automationProbability}
          />
          <ProbabilityMeter probability={selectedJob.automationProbability} />
        </div>

        <div className="visualization-row">
          <JobIcon3D jobId={selectedJob.id} riskLevel={selectedJob.riskLevel} />
          <Timeline milestones={selectedJob.milestones} />
        </div>

        <div className="visualization-row">
          <AdaptationStrategies jobId={selectedJob.id} />
        </div>
      </div>
    </motion.div>
  );
};

export default JobDetails;
