import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { useJob } from '../contexts/JobContext';
import '../styles/JobImpact.css';

// 默认技能列表，当职业没有特定技能时使用
const defaultSkills = {
  technical: [
    'AI 工具使用与集成能力',
    '数据分析与解读能力',
    '数字化工作流程掌握',
    '新技术快速学习能力'
  ],
  cognitive: [
    '复杂问题解决能力',
    '系统性思维方式',
    '创新与创造性思维',
    '跨领域知识整合'
  ],
  social: [
    '人机协作与沟通',
    '团队协作与领导力',
    '情商与共情能力',
    '跨文化交流能力'
  ],
  adaptability: [
    '持续学习意愿',
    '变革管理能力',
    '职业规划能力',
    '压力管理能力'
  ]
};

const JobImpact: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob } = useJob();

  if (!selectedJob) {
    return null;
  }

  // 使用职业特定技能或默认技能
  const skills = selectedJob.skillsNeeded || defaultSkills;

  return (
    <motion.div 
      className="job-impact"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="impact-header">
        <h3>技能提升指南</h3>
        <p className="impact-description">AI 时代必备的核心能力与学习方向</p>
      </div>

      <div className="impact-content">
        <div className="impact-details">
          <div className="impact-section technical">
            <div className="section-header">
              <h4>技术能力</h4>
              <span className="section-icon">💻</span>
            </div>
            <ul>
              {skills.technical.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>

          <div className="impact-section cognitive">
            <div className="section-header">
              <h4>认知能力</h4>
              <span className="section-icon">🧠</span>
            </div>
            <ul>
              {skills.cognitive.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>

          <div className="impact-section social">
            <div className="section-header">
              <h4>社交能力</h4>
              <span className="section-icon">🤝</span>
            </div>
            <ul>
              {skills.social.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>

          <div className="impact-section adaptability">
            <div className="section-header">
              <h4>适应能力</h4>
              <span className="section-icon">🔄</span>
            </div>
            <ul>
              {skills.adaptability.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default JobImpact;
