    'Ë∑®È¢ÜÂüüÁü•ËØÜÊï¥Âêà'
  ]
};

const JobImpact: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob } = useJob();

  if (!selectedJob) {
    return null;
