import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Job } from '../types/types';
import { useJob } from '../contexts/JobContext';
import { useLanguage } from '../contexts/LanguageContext';

const JobSearch: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob, setSelectedJob, availableJobs } = useJob();
  const [searchTerm, setSearchTerm] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [filteredJobs, setFilteredJobs] = useState<Job[]>([]);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const [userEditing, setUserEditing] = useState(false);

  // 处理搜索输入变化
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchTerm(value);
    setUserEditing(true);

    if (value.trim() === '') {
      setFilteredJobs(availableJobs);
    } else {
      const filtered = availableJobs.filter((job: Job) => 
        job.title.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredJobs(filtered);
    }

    setIsDropdownOpen(true);
  };

  // 处理职业选择
  const handleJobSelect = (job: Job) => {
    console.log('Selected job in JobSearch:', job.title, 'with ID:', job.id);
    setSearchTerm(job.title);
    setSelectedJob(job);
    setIsDropdownOpen(false);
    setUserEditing(false);
  };

  // 处理下拉菜单切换
  const toggleDropdown = () => {
    if (isDropdownOpen) {
      setIsDropdownOpen(false);
    } else {
      setFilteredJobs(availableJobs);
      setIsDropdownOpen(true);
    }
  };

  // 点击外部关闭下拉菜单
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // 当输入框失去焦点且为空时，恢复显示当前选中的职业
  const handleInputBlur = () => {
    if (searchTerm.trim() === '' && selectedJob) {
      setSearchTerm(selectedJob.title);
      setUserEditing(false);
    }
  };

  // 当下拉框打开时，加载所有职业
  useEffect(() => {
    if (isDropdownOpen) {
      if (searchTerm.trim() === '') {
        setFilteredJobs(availableJobs);
      } else {
        const filtered = availableJobs.filter((job: Job) => 
          job.title.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredJobs(filtered);
      }
    }
  }, [isDropdownOpen, availableJobs, searchTerm]);

  return (
    <motion.div 
      className="job-search"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2>{t('search.title')}</h2>
      <div className="search-container" ref={dropdownRef}>
        <div className="search-input-wrapper">
          <span className="search-icon">🔍</span>
          <input
            type="text"
            className="search-input"
            placeholder={t('search.placeholder')}
            value={searchTerm}
            onChange={handleSearchChange}
            onBlur={handleInputBlur}
            onClick={() => {
              setFilteredJobs(availableJobs);
              setIsDropdownOpen(true);
            }}
          />
          <button className="dropdown-toggle" onClick={toggleDropdown}>
            {isDropdownOpen ? '▲' : '▼'}
          </button>
        </div>

        <AnimatePresence>
          {isDropdownOpen && (
            <motion.ul
              className="dropdown-list"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <li 
                    key={job.id} 
                    className="dropdown-item"
                    onClick={() => handleJobSelect(job)}
                  >
                    {job.title}
                  </li>
                ))
              ) : (
                <li className="dropdown-item no-results">{t('search.noResults')}</li>
              )}
            </motion.ul>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
};

export default JobSearch;
