import React, { useEffect, useState, useCallback, useMemo, useRef } from 'react';
import { motion } from 'framer-motion';
import * as THREE from 'three';
import { Canvas, useFrame } from '@react-three/fiber';

interface CountdownClockProps {
  predictedYear: number;
}

interface TimeLeft {
  years: number;
  months: number;
  days: number;
  hours: number;
}

interface Particle {
  id: number;
  x: number;
  y: number;
  size: number;
  speed: number;
}

// 添加裂纹特效组件
const CrackEffect: React.FC<{ automationProbability: number }> = ({ automationProbability }) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const crackTexture = useMemo(() => {
    const loader = new THREE.TextureLoader();
    return loader.load('/textures/crack.png'); // 需要添加裂纹纹理图片
  }, []);

  const showCracks = automationProbability > 70;

  useFrame(({ clock }) => {
    if (meshRef.current && showCracks) {
      // 裂纹动画效果
      meshRef.current.material.opacity = 0.3 + Math.sin(clock.getElapsedTime() * 2) * 0.2;
    }
  });

  if (!showCracks) return null;

  return (
    <mesh ref={meshRef} position={[0, 0, 0.1]}>
      <planeGeometry args={[2, 2]} />
      <meshBasicMaterial 
        transparent={true} 
        map={crackTexture} 
        opacity={0.5} 
        depthWrite={false}
      />
    </mesh>
  );
};

const CountdownClock: React.FC<CountdownClockProps & { automationProbability: number }> = 
  ({ predictedYear, automationProbability }) => {
  const [timeLeft, setTimeLeft] = useState<TimeLeft>({
    years: 0,
    months: 0,
    days: 0,
    hours: 0,
  });

  const [particles, setParticles] = useState<Particle[]>([]);

  // 计算倒计时
  const calculateTimeLeft = useCallback(() => {
    const now = new Date();
    const targetDate = new Date(predictedYear, 0, 1);
    const difference = targetDate.getTime() - now.getTime();

    if (difference > 0) {
      const years = Math.floor(difference / (1000 * 60 * 60 * 24 * 365));
      const months = Math.floor((difference % (1000 * 60 * 60 * 24 * 365)) / (1000 * 60 * 60 * 24 * 30));
      const days = Math.floor((difference % (1000 * 60 * 60 * 24 * 30)) / (1000 * 60 * 60 * 24));
      const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

      setTimeLeft({ years, months, days, hours });
    }
  }, [predictedYear]);

  useEffect(() => {
    // 生成随机粒子
    const newParticles = Array.from({ length: 50 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      size: Math.random() * 3 + 1,
      speed: Math.random() * 2 + 0.5
    }));
    setParticles(newParticles);

    calculateTimeLeft();
    const timer = setInterval(calculateTimeLeft, 1000 * 60 * 60); // 每小时更新一次

    return () => clearInterval(timer);
  }, [predictedYear, calculateTimeLeft]);

  // 动画粒子
  useEffect(() => {
    let animationFrameId: number;

    const animateParticles = () => {
      setParticles(prevParticles => 
        prevParticles.map(particle => ({
          ...particle,
          y: (particle.y + particle.speed) % 100
        }))
      );
      animationFrameId = requestAnimationFrame(animateParticles);
    };

    animationFrameId = requestAnimationFrame(animateParticles);

    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
    };
  }, []);

  return (
    <motion.div 
      className="countdown-clock"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.8 }}
    >
      <h3>AI显著影响预计时间</h3>
      <div className="clock-container">
        {particles.map(particle => (
          <motion.div
            key={particle.id}
            className="particle"
            style={{
              left: `${particle.x}%`,
              top: `${particle.y}%`,
              width: `${particle.size}px`,
              height: `${particle.size}px`,
            }}
            animate={{
              opacity: [0.4, 1, 0.4],
              scale: [1, 1.2, 1],
            }}
            transition={{
              duration: 2 + Math.random() * 2,
              repeat: Infinity,
              repeatType: "reverse"
            }}
          />
        ))}

        {/* 添加3D时钟效果 */}
        <div className="clock-3d" style={{ position: 'absolute', width: '100%', height: '100%', zIndex: 1 }}>
          <Canvas>
            <ambientLight intensity={0.5} />
            <CrackEffect automationProbability={automationProbability} />
          </Canvas>
        </div>

        <div className="time-display">
          <div className="time-unit">
            <span className="time-value">{timeLeft.years}</span>
            <span className="time-label">年</span>
          </div>
          <div className="time-unit">
            <span className="time-value">{timeLeft.months}</span>
            <span className="time-label">月</span>
          </div>
          <div className="time-unit">
            <span className="time-value">{timeLeft.days}</span>
            <span className="time-label">日</span>
          </div>
          <div className="time-unit">
            <span className="time-value">{timeLeft.hours}</span>
            <span className="time-label">时</span>
          </div>
        </div>
        <div className="target-year">目标年份: {predictedYear}</div>
      </div>
    </motion.div>
  );
};

export default CountdownClock;
