import React, { useState } from 'react';
import { motion } from 'framer-motion';
import JobSearch from './components/JobSearch';
import CountdownClock from './components/CountdownClock';
import ProbabilityMeter from './components/ProbabilityMeter';
import JobIcon3D from './components/JobIcon3D';
import Timeline from './components/Timeline';
import AdaptationStrategies from './components/AdaptationStrategies';
import StarfieldBackground from './components/StarfieldBackground';
import AlertOverlay from './components/AlertOverlay';
import { Job } from './types/types';
import './App.css';

const App: React.FC = () => {
  const [selectedJob, setSelectedJob] = useState<Job | null>(null);
  const [showAdmin, setShowAdmin] = useState(false);

  // 简单的管理员访问控制
  const handleAdminAccess = () => {
    const password = prompt('请输入管理员密码:');
    if (password === 'admin123') {
      setShowAdmin(true);
    } else {
      alert('密码错误');
    }
  };

  // 管理员视图
  if (showAdmin) {
    return (
      <AdminPanel onBack={() => setShowAdmin(false)} />
    );
  }

  return (
    <div className="app">
      {selectedJob && (
        <>
          <StarfieldBackground predictedYear={selectedJob.predictedYear} />
          <AlertOverlay predictedYear={selectedJob.predictedYear} />
        </>
      )}

      <motion.header
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h1>AI时代职业规划助手</h1>
        <p>探索人工智能对不同职业的影响，规划你的职业未来</p>
      </motion.header>

      <main>
        <JobSearch onSelectJob={setSelectedJob} />

        {selectedJob && (
          <motion.div 
            className="job-details"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <h2>{selectedJob.title}</h2>

            <div className="data-visualization">
              <div className="visualization-row">
                <CountdownClock 
                  predictedYear={selectedJob.predictedYear} 
                  automationProbability={selectedJob.automationProbability}
                />
                <ProbabilityMeter probability={selectedJob.automationProbability} />
              </div>

              <div className="visualization-row">
                <JobIcon3D jobId={selectedJob.id} riskLevel={selectedJob.riskLevel} />
                <Timeline milestones={selectedJob.milestones} />
              </div>

              <div className="visualization-row">
                <AdaptationStrategies jobId={selectedJob.id} />
              </div>
            </div>
          </motion.div>
        )}
      </main>

      <footer>
        <p>数据来源：基于当前AI发展趋势的预测分析</p>
        <div className="footer-links">
          <p>© {new Date().getFullYear()} AI时代职业规划助手</p>
          <button 
            className="admin-link" 
            onClick={handleAdminAccess}
          >
            管理员入口
          </button>
        </div>
      </footer>
    </div>
  );
};

// 内联管理员面板组件
const AdminPanel: React.FC<{ onBack: () => void }> = ({ onBack }) => {
  const [submissions, setSubmissions] = useState<Array<{
    id: string;
    title: string;
    timestamp: string;
  }>>([]);

  React.useEffect(() => {
    // 从本地存储加载提交的职业
    const savedSubmissions = JSON.parse(localStorage.getItem('jobSubmissions') || '[]');
    setSubmissions(savedSubmissions);
  }, []);

  const handleClearSubmissions = () => {
    if (window.confirm('确定要清空所有提交记录吗？')) {
      localStorage.removeItem('jobSubmissions');
      setSubmissions([]);
    }
  };

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h2>用户提交的职业</h2>
        <div className="admin-actions">
          <button onClick={handleClearSubmissions} className="clear-button">
            清空记录
          </button>
          <button onClick={onBack} className="back-button">
            返回首页
          </button>
        </div>
      </div>

      {submissions.length > 0 ? (
        <div className="submissions-list">
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>职业名称</th>
                <th>提交时间</th>
              </tr>
            </thead>
            <tbody>
              {submissions.map(submission => (
                <tr key={submission.id}>
                  <td>{submission.id}</td>
                  <td>{submission.title}</td>
                  <td>{new Date(submission.timestamp).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="no-submissions">
          <p>暂无提交记录</p>
        </div>
      )}
    </div>
  );
};

export default App;
