import React, { useEffect, useState, useCallback } from 'react';
import { motion } from 'framer-motion';

interface ProbabilityMeterProps {
  probability: number; // 0-100
}

interface Particle {
  id: number;
  angle: number;
  distance: number;
  size: number;
  speed: number;
}

const ProbabilityMeter: React.FC<ProbabilityMeterProps> = ({ probability }) => {
  const [particles, setParticles] = useState<Particle[]>([]);

  // 计算圆环上的坐标
  const getCoordinates = useCallback((angle: number, distance: number) => {
    const radius = 100; // 圆环半径
    const x = 50 + Math.cos(angle) * radius * distance / 100;
    const y = 50 + Math.sin(angle) * radius * distance / 100;
    return { x, y };
  }, []);

  useEffect(() => {
    // 生成沿着进度条的粒子
    const particleCount = Math.floor(probability / 2) + 5; // 根据概率调整粒子数量
    const maxAngle = (probability / 100) * Math.PI * 2;

    const newParticles = Array.from({ length: particleCount }, (_, i) => {
      const angle = Math.random() * maxAngle;
      return {
        id: i,
        angle,
        distance: 0.9 + Math.random() * 0.2, // 距离圆心的距离（0.9-1.1倍半径）
        size: Math.random() * 4 + 2,
        speed: (Math.random() * 0.01 + 0.005) * (Math.random() > 0.5 ? 1 : -1) // 粒子移动速度
      };
    });

    setParticles(newParticles);
  }, [probability]);

  // 动画粒子
  useEffect(() => {
    let animationFrameId: number;

    const animateParticles = () => {
      setParticles(prevParticles => 
        prevParticles.map(particle => {
          const newAngle = (particle.angle + particle.speed) % (Math.PI * 2);
          // 确保粒子只在进度条范围内移动
          const maxAngle = (probability / 100) * Math.PI * 2;
          return {
            ...particle,
            angle: newAngle > 0 && newAngle < maxAngle ? newAngle : 
                  newAngle <= 0 ? maxAngle - 0.01 : 0.01
          };
        })
      );
      animationFrameId = requestAnimationFrame(animateParticles);
    };

    animationFrameId = requestAnimationFrame(animateParticles);

    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
    };
  }, [probability]);

  // 确定颜色
  const getColor = useCallback(() => {
    if (probability < 30) return '#4CAF50'; // 绿色 - 低风险
    if (probability < 70) return '#FF9800'; // 橙色 - 中风险
    return '#F44336'; // 红色 - 高风险
  }, [probability]);

  const circumference = 2 * Math.PI * 90; // 圆环周长
  const strokeDashoffset = circumference * (1 - probability / 100);

  return (
    <motion.div 
      className="probability-meter"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: 0.8, delay: 0.2 }}
    >
      <h3>AI影响程度</h3>
      <div className="meter-container">
        <svg width="200" height="200" viewBox="0 0 200 200">
          {/* 背景圆环 */}
          <circle
            cx="100"
            cy="100"
            r="90"
            fill="none"
            stroke="#e0e0e0"
            strokeWidth="10"
          />

          {/* 进度圆环 */}
          <motion.circle
            cx="100"
            cy="100"
            r="90"
            fill="none"
            stroke={getColor()}
            strokeWidth="10"
            strokeDasharray={circumference}
            initial={{ strokeDashoffset: circumference }}
            animate={{ strokeDashoffset }}
            transition={{ duration: 1.5, ease: "easeOut" }}
            transform="rotate(-90 100 100)"
            strokeLinecap="round"
          />

          {/* 粒子 */}
          {particles.map(particle => {
            const { x, y } = getCoordinates(particle.angle, particle.distance);
            return (
              <motion.circle
                key={particle.id}
                cx={`${x}%`}
                cy={`${y}%`}
                r={particle.size}
                fill={getColor()}
                animate={{
                  opacity: [0.4, 1, 0.4],
                  r: [particle.size, particle.size * 1.5, particle.size],
                }}
                transition={{
                  duration: 2 + Math.random() * 2,
                  repeat: Infinity,
                  repeatType: "reverse"
                }}
              />
            );
          })}

          {/* 概率文本 */}
          <text
            x="100"
            y="100"
            textAnchor="middle"
            dominantBaseline="middle"
            fontSize="36"
            fontWeight="bold"
            fill={getColor()}
          >
            {probability}%
          </text>
        </svg>
      </div>
    </motion.div>
  );
};

export default ProbabilityMeter;
