import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { useJob } from '../contexts/JobContext';

const AdaptationStrategies: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob } = useJob();

  if (!selectedJob?.adaptationStrategies) {
    return null;
  }

  const { skills, courses, alternatives } = selectedJob.adaptationStrategies;

  return (
    <motion.div 
      className="adaptation-strategies"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="strategies-content">
        <div className="strategy-section">
          <h4>{t('adaptation.skillsTitle')}</h4>
          <p>{t('adaptation.skillsDesc')}</p>
          <ul>
            {skills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
        </div>

        <div className="strategy-section">
          <h4>{t('adaptation.coursesTitle')}</h4>
          <p>{t('adaptation.coursesDesc')}</p>
          <ul>
            {courses.map((course, index) => (
              <li key={index}>{course}</li>
            ))}
          </ul>
        </div>

        <div className="strategy-section">
          <h4>{t('adaptation.alternativeTitle')}</h4>
          <p>{t('adaptation.alternativeDesc')}</p>
          <ul>
            {alternatives.map((alternative, index) => (
              <li key={index}>{alternative}</li>
            ))}
          </ul>
        </div>
      </div>
    </motion.div>
  );
};

export default AdaptationStrategies;
