import React, { useEffect, useState, useCallback, useMemo, useRef } from 'react';
import { motion } from 'framer-motion';
import * as THREE from 'three';
import { Canvas, useFrame } from '@react-three/fiber';

interface CountdownClockProps {
  predictedYear: number;
  automationProbability: number;
}

interface TimeLeft {
  years: number;
  months: number;
  days: number;
  hours: number;
}

interface Particle {
  id: number;
  x: number;
  y: number;
  size: number;
  speed: number;
}

// 裂纹效果组件
const CrackEffect: React.FC<{ automationProbability: number }> = ({ automationProbability }) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const showCracks = automationProbability > 70;

  // 创建裂纹纹理
  const crackPattern = useMemo(() => {
    const canvas = document.createElement('canvas');
    canvas.width = 256;
    canvas.height = 256;
    const ctx = canvas.getContext('2d');

    if (ctx) {
      ctx.fillStyle = 'rgba(0, 0, 0, 0)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // 绘制裂纹
      const cracks = Math.floor(5 + Math.random() * 5);

      for (let i = 0; i < cracks; i++) {
        const startX = canvas.width / 2;
        const startY = canvas.height / 2;

        ctx.beginPath();
        ctx.moveTo(startX, startY);

        let x = startX;
        let y = startY;
        const segments = 10 + Math.floor(Math.random() * 10);

        for (let j = 0; j < segments; j++) {
          const angle = Math.random() * Math.PI * 2;
          const length = 10 + Math.random() * 20;

          x += Math.cos(angle) * length;
          y += Math.sin(angle) * length;

          ctx.lineTo(x, y);
        }

        ctx.strokeStyle = `rgba(255, 50, 50, ${0.5 + Math.random() * 0.5})`;
        ctx.lineWidth = 1 + Math.random() * 2;
        ctx.stroke();
      }
    }

    return new THREE.CanvasTexture(canvas);
  }, []);

  useFrame(({ clock }) => {
    if (meshRef.current && showCracks) {
      // 裂纹动画效果
      (meshRef.current.material as THREE.MeshBasicMaterial).opacity = 
        0.3 + Math.sin(clock.getElapsedTime() * 2) * 0.2;
    }
  });

  // 如果不显示裂纹，返回null
  if (!showCracks) return null;

  return (
    <mesh ref={meshRef} position={[0, 0, 0.1]}>
      <planeGeometry args={[2, 2]} />
      <meshBasicMaterial 
        transparent={true} 
        map={crackPattern} 
        opacity={0.5} 
        depthWrite={false}
      />
    </mesh>
  );
};

// 时钟表面组件
const ClockFace: React.FC<{ timeLeft: TimeLeft, predictedYear: number, automationProbability: number }> = 
  ({ timeLeft, predictedYear, automationProbability }) => {
  const meshRef = useRef<THREE.Mesh>(null);
  const materialRef = useRef<THREE.ShaderMaterial>(null);

  // 创建时钟表面着色器
  const clockShader = useMemo(() => {
    return {
      uniforms: {
        time: { value: 0 },
        color: { value: new THREE.Color(automationProbability > 70 ? '#ff3333' : '#2196F3') },
        progress: { value: Math.min(1, automationProbability / 100) }
      },
      vertexShader: `
        varying vec2 vUv;
        void main() {
          vUv = uv;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragmentShader: `
        uniform float time;
        uniform vec3 color;
        uniform float progress;
        varying vec2 vUv;

        void main() {
          vec2 center = vec2(0.5, 0.5);
          float dist = distance(vUv, center);

          // 创建时钟表面
          float circle = smoothstep(0.45, 0.4, dist);

          // 创建时钟边框
          float ring = smoothstep(0.45, 0.44, dist) - smoothstep(0.4, 0.39, dist);

          // 创建刻度
          float ticks = 0.0;
          for(int i = 0; i < 12; i++) {
            float angle = float(i) * 3.14159 * 2.0 / 12.0;
            vec2 tickPos = center + vec2(cos(angle), sin(angle)) * 0.4;
            float tickDist = distance(vUv, tickPos);
            ticks += smoothstep(0.03, 0.02, tickDist);
          }

          // 创建进度指示器
          float angle = -3.14159 * 0.5 + progress * 3.14159 * 2.0;
          vec2 handEnd = center + vec2(cos(angle), sin(angle)) * 0.35;
          vec2 handDir = normalize(handEnd - center);
          float handDist = distance(vUv, mix(center, handEnd, clamp(dot(vUv - center, handDir) / dot(handEnd - center, handDir), 0.0, 1.0)));
          float hand = smoothstep(0.03, 0.02, handDist);

          // 脉冲效果
          float pulse = sin(time * 3.0) * 0.5 + 0.5;
          vec3 pulseColor = mix(color, vec3(1.0), pulse * 0.3);

          // 组合所有元素
          vec3 finalColor = mix(vec3(0.1), pulseColor, circle);
          finalColor = mix(finalColor, vec3(0.8), ring);
          finalColor = mix(finalColor, vec3(1.0), ticks);
          finalColor = mix(finalColor, pulseColor, hand);

          gl_FragColor = vec4(finalColor, 1.0);
        }
      `
    };
  }, [automationProbability]);

  useFrame(({ clock }) => {
    if (materialRef.current) {
      materialRef.current.uniforms.time.value = clock.getElapsedTime();
    }
  });

  return (
    <mesh ref={meshRef} position={[0, 0, 0]}>
      <planeGeometry args={[1.8, 1.8]} />
      <shaderMaterial 
        ref={materialRef}
        args={[clockShader]}
        transparent={true}
      />
    </mesh>
  );
};

// 主组件
const CountdownClock: React.FC<CountdownClockProps> = ({ predictedYear, automationProbability }) => {
  const [timeLeft, setTimeLeft] = useState<TimeLeft>({
    years: 0,
    months: 0,
    days: 0,
    hours: 0
  });

  const calculateTimeLeft = useCallback(() => {
    const now = new Date();
    const targetDate = new Date(predictedYear, 0, 1);
    const difference = targetDate.getTime() - now.getTime();

    if (difference > 0) {
      const days = Math.floor(difference / (1000 * 60 * 60 * 24));
      const years = Math.floor(days / 365);
      const months = Math.floor((days % 365) / 30);
      const remainingDays = days % 30;
      const hours = Math.floor((difference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

      setTimeLeft({
        years,
        months,
        days: remainingDays,
        hours
      });
    } else {
      setTimeLeft({
        years: 0,
        months: 0,
        days: 0,
        hours: 0
      });
    }
  }, [predictedYear]);

  useEffect(() => {
    calculateTimeLeft();
    const timer = setInterval(calculateTimeLeft, 1000 * 60 * 60); // 每小时更新一次

    return () => {
      clearInterval(timer);
    };
  }, [calculateTimeLeft]);

  // 获取紧迫感颜色
  const getUrgencyColor = () => {
    if (timeLeft.years <= 2) return '#F44336'; // 红色 - 非常紧急
    if (timeLeft.years <= 5) return '#FF9800'; // 橙色 - 中等紧急
    return '#2196F3'; // 蓝色 - 不太紧急
  };

  // 获取紧迫感文本
  const getUrgencyText = () => {
    if (timeLeft.years <= 2) return '高度紧迫';
    if (timeLeft.years <= 5) return '中度紧迫';
    return '低度紧迫';
  };

  return (
    <motion.div 
      className="countdown-clock"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <h3>AI显著影响预计时间</h3>

      <div className="clock-container">
        <Canvas camera={{ position: [0, 0, 2] }}>
          <ambientLight intensity={0.5} />
          <ClockFace 
            timeLeft={timeLeft} 
            predictedYear={predictedYear} 
            automationProbability={automationProbability} 
          />
          <CrackEffect automationProbability={automationProbability} />
        </Canvas>

        <div className="time-display">
          <div className="time-units">
            <div className="time-unit">
              <motion.span 
                className="time-value"
                key={timeLeft.years}
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 15 }}
                style={{ color: getUrgencyColor() }}
              >
                {timeLeft.years}
              </motion.span>
              <span className="time-label">年</span>
            </div>
            <div className="time-unit">
              <motion.span 
                className="time-value"
                key={timeLeft.months}
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 15, delay: 0.1 }}
                style={{ color: getUrgencyColor() }}
              >
                {timeLeft.months}
              </motion.span>
              <span className="time-label">月</span>
            </div>
            <div className="time-unit">
              <motion.span 
                className="time-value"
                key={timeLeft.days}
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 15, delay: 0.2 }}
                style={{ color: getUrgencyColor() }}
              >
                {timeLeft.days}
              </motion.span>
              <span className="time-label">日</span>
            </div>
            <div className="time-unit">
              <motion.span 
                className="time-value"
                key={timeLeft.hours}
                initial={{ y: -20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ type: "spring", stiffness: 300, damping: 15, delay: 0.3 }}
                style={{ color: getUrgencyColor() }}
              >
                {timeLeft.hours}
              </motion.span>
              <span className="time-label">时</span>
            </div>
          </div>

          <motion.div 
            className="urgency-indicator"
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ type: "spring", stiffness: 260, damping: 20, delay: 0.5 }}
            style={{ 
              backgroundColor: `${getUrgencyColor()}20`,
              borderColor: getUrgencyColor()
            }}
          >
            <span className="urgency-dot" style={{ backgroundColor: getUrgencyColor() }}></span>
            <span className="urgency-text" style={{ color: getUrgencyColor() }}>
              {getUrgencyText()}
            </span>
          </motion.div>
        </div>

        <div className="target-year">目标年份: {predictedYear}</div>
      </div>
    </motion.div>
  );
};

export default CountdownClock;
