import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

interface ProbabilityMeterProps {
  probability: number; // 0-100
}

const ProbabilityMeter: React.FC<ProbabilityMeterProps> = ({ probability }) => {
  const [animatedValue, setAnimatedValue] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimatedValue(probability);
    }, 500);

    return () => clearTimeout(timer);
  }, [probability]);

  // 根据概率值确定颜色
  const getColor = () => {
    if (probability >= 80) return '#F44336'; // 红色 - 高风险
    if (probability >= 50) return '#FF9800'; // 橙色 - 中风险
    return '#4CAF50'; // 绿色 - 低风险
  };

  // 根据概率值确定风险等级文本
  const getRiskLevelText = () => {
    if (probability >= 80) return '高度风险';
    if (probability >= 50) return '中度风险';
    return '低度风险';
  };

  // 根据概率值确定描述文本
  const getDescriptionText = () => {
    if (probability >= 80) {
      return '该职业极有可能被AI完全自动化，建议尽快转型或提升技能。';
    } else if (probability >= 50) {
      return '该职业有较大可能被部分自动化，需要适应AI辅助工作模式。';
    } else {
      return '该职业短期内受AI影响较小，但仍需关注技术发展趋势。';
    }
  };

  // 获取职业适应性建议
  const getAdaptationAdvice = () => {
    if (probability >= 80) {
      return [
        '学习与AI协作的新技能',
        '发展AI难以替代的创造性能力',
        '考虑向相关但更安全的职业转型'
      ];
    } else if (probability >= 50) {
      return [
        '掌握AI工具的使用方法',
        '专注于需要人类判断的工作环节',
        '提升沟通和协作等软技能'
      ];
    } else {
      return [
        '关注行业AI应用的最新发展',
        '学习如何利用AI提高工作效率',
        '发展跨领域专业知识'
      ];
    }
  };

  // 计算仪表盘指针角度
  const needleRotation = -90 + (animatedValue / 100) * 180;

  return (
    <motion.div 
      className="probability-meter"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8, delay: 0.2 }}
    >
      <h3>AI 影响程度</h3>

      <div className="meter-content">
        <div className="gauge-container">
          <div className="gauge">
            <div className="gauge-background">
              <div className="gauge-indicator low" />
              <div className="gauge-indicator medium" />
              <div className="gauge-indicator high" />

              <div 
                className="gauge-needle"
                style={{ 
                  transform: `rotate(${needleRotation}deg)`,
                  backgroundColor: getColor()
                }}
              >
                <div className="needle-cap" />
              </div>
            </div>

            <div className="gauge-value">
              <motion.span
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.8 }}
              >
                {animatedValue}%
              </motion.span>
            </div>

            <div className="gauge-labels">
              <span>低</span>
              <span>中</span>
              <span>高</span>
            </div>
          </div>
        </div>

        <div className="risk-assessment">
          <div className="risk-level" style={{ color: getColor() }}>
            {getRiskLevelText()}
          </div>

          <motion.div 
            className="risk-description"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 1 }}
          >
            {getDescriptionText()}
          </motion.div>
        </div>

        <div className="impact-areas">
          <h4>主要影响领域:</h4>
          <ul>
            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.2 }}
            >
              <span className="impact-icon">🔄</span>
              <span>工作流程自动化</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability + 10)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>

            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.4 }}
            >
              <span className="impact-icon">🧠</span>
              <span>决策辅助</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability - 5)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>

            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.6 }}
            >
              <span className="impact-icon">👥</span>
              <span>人际互动替代</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability - 15)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>
          </ul>
        </div>

        <div className="adaptation-advice">
          <h4>职业适应性建议:</h4>
          <ul>
            {getAdaptationAdvice().map((advice, index) => (
              <motion.li 
                key={index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.3, delay: 1.8 + index * 0.2 }}
              >
                <span className="advice-icon">✓</span>
                {advice}
              </motion.li>
            ))}
          </ul>
        </div>
      </div>
    </motion.div>
  );
};

export default ProbabilityMeter;
