import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

interface ProbabilityMeterProps {
  probability: number; // 0-100
}

const ProbabilityMeter: React.FC<ProbabilityMeterProps> = ({ probability }) => {
  const [animatedValue, setAnimatedValue] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimatedValue(probability);
    }, 500);

    return () => clearTimeout(timer);
  }, [probability]);

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÈ¢úËâ≤
  const getColor = () => {
    if (probability >= 80) return '#F44336'; // Á∫¢Ëâ≤ - È´òÈ£éÈô©
    if (probability >= 50) return '#FF9800'; // Ê©ôËâ≤ - ‰∏≠È£éÈô©
    return '#4CAF50'; // ÁªøËâ≤ - ‰ΩéÈ£éÈô©
  };

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÈ£éÈô©Á≠âÁ∫ßÊñáÊú¨
  const getRiskLevelText = () => {
    if (probability >= 80) return 'È´òÂ∫¶È£éÈô©';
    if (probability >= 50) return '‰∏≠Â∫¶È£éÈô©';
    return '‰ΩéÂ∫¶È£éÈô©';
  };

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÊèèËø∞ÊñáÊú¨
  const getDescriptionText = () => {
    if (probability >= 80) {
      return 'ËØ•ËÅå‰∏öÊûÅÊúâÂèØËÉΩË¢´AIÂÆåÂÖ®Ëá™Âä®ÂåñÔºåÂª∫ËÆÆÂ∞ΩÂø´ËΩ¨ÂûãÊàñÊèêÂçáÊäÄËÉΩ„ÄÇ';
    } else if (probability >= 50) {
      return 'ËØ•ËÅå‰∏öÊúâËæÉÂ§ßÂèØËÉΩË¢´ÈÉ®ÂàÜËá™Âä®ÂåñÔºåÈúÄË¶ÅÈÄÇÂ∫îAIËæÖÂä©Â∑•‰ΩúÊ®°Âºè„ÄÇ';
    } else {
      return 'ËØ•ËÅå‰∏öÁü≠ÊúüÂÜÖÂèóAIÂΩ±ÂìçËæÉÂ∞èÔºå‰ΩÜ‰ªçÈúÄÂÖ≥Ê≥®ÊäÄÊúØÂèëÂ±ïË∂ãÂäø„ÄÇ';
    }
  };

  // Ëé∑ÂèñËÅå‰∏öÈÄÇÂ∫îÊÄßÂª∫ËÆÆ
  const getAdaptationAdvice = () => {
    if (probability >= 80) {
      return [
        'Â≠¶‰π†‰∏éAIÂçè‰ΩúÁöÑÊñ∞ÊäÄËÉΩ',
        'ÂèëÂ±ïAIÈöæ‰ª•Êõø‰ª£ÁöÑÂàõÈÄ†ÊÄßËÉΩÂäõ',
        'ËÄÉËôëÂêëÁõ∏ÂÖ≥‰ΩÜÊõ¥ÂÆâÂÖ®ÁöÑËÅå‰∏öËΩ¨Âûã'
      ];
    } else if (probability >= 50) {
      return [
        'ÊéåÊè°AIÂ∑•ÂÖ∑ÁöÑ‰ΩøÁî®ÊñπÊ≥ï',
        '‰∏ìÊ≥®‰∫éÈúÄË¶Å‰∫∫Á±ªÂà§Êñ≠ÁöÑÂ∑•‰ΩúÁéØËäÇ',
        'ÊèêÂçáÊ≤üÈÄöÂíåÂçè‰ΩúÁ≠âËΩØÊäÄËÉΩ'
      ];
    } else {
      return [
        'ÂÖ≥Ê≥®Ë°å‰∏öAIÂ∫îÁî®ÁöÑÊúÄÊñ∞ÂèëÂ±ï',
        'Â≠¶‰π†Â¶Ç‰ΩïÂà©Áî®AIÊèêÈ´òÂ∑•‰ΩúÊïàÁéá',
        'ÂèëÂ±ïË∑®È¢ÜÂüü‰∏ì‰∏öÁü•ËØÜ'
      ];
    }
  };

  // ËÆ°ÁÆó‰ª™Ë°®ÁõòÊåáÈíàËßíÂ∫¶
  const needleRotation = -90 + (animatedValue / 100) * 180;

  return (
    <motion.div 
      className="probability-meter"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8, delay: 0.2 }}
    >
      <h3>AI ÂΩ±ÂìçÁ®ãÂ∫¶</h3>

      <div className="meter-content">
        <div className="gauge-container">
          <div className="gauge">
            <div className="gauge-background">
              <div className="gauge-indicator low" />
              <div className="gauge-indicator medium" />
              <div className="gauge-indicator high" />

              <div 
                className="gauge-needle"
                style={{ 
                  transform: `rotate(${needleRotation}deg)`,
                  backgroundColor: getColor()
                }}
              >
                <div className="needle-cap" />
              </div>
            </div>

            <div className="gauge-value">
              <motion.span
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.8 }}
              >
                {animatedValue}%
              </motion.span>
            </div>

            <div className="gauge-labels">
              <span>‰Ωé</span>
              <span>‰∏≠</span>
              <span>È´ò</span>
            </div>
          </div>
        </div>

        <div className="risk-assessment">
          <div className="risk-level" style={{ color: getColor() }}>
            {getRiskLevelText()}
          </div>

          <motion.div 
            className="risk-description"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 1 }}
          >
            {getDescriptionText()}
          </motion.div>
        </div>

        <div className="impact-areas">
          <h4>‰∏ªË¶ÅÂΩ±ÂìçÈ¢ÜÂüü:</h4>
          <ul>
            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.2 }}
            >
              <span className="impact-icon">üîÑ</span>
              <span>Â∑•‰ΩúÊµÅÁ®ãËá™Âä®Âåñ</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability + 10)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>

            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.4 }}
            >
              <span className="impact-icon">üß†</span>
              <span>ÂÜ≥Á≠ñËæÖÂä©</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability - 5)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>

            <motion.li 
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: 1.6 }}
            >
              <span className="impact-icon">üë•</span>
              <span>‰∫∫ÈôÖ‰∫íÂä®Êõø‰ª£</span>
              <span className="impact-level" style={{ 
                width: `${Math.min(100, probability - 15)}%`,
                backgroundColor: getColor()
              }} />
            </motion.li>
          </ul>
        </div>

        <div className="adaptation-advice">
          <h4>ËÅå‰∏öÈÄÇÂ∫îÊÄßÂª∫ËÆÆ:</h4>
          <ul>
            {getAdaptationAdvice().map((advice, index) => (
              <motion.li 
                key={index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.3, delay: 1.8 + index * 0.2 }}
              >
                <span className="advice-icon">‚úì</span>
                {advice}
              </motion.li>
            ))}
          </ul>
        </div>
      </div>
    </motion.div>
