import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

interface ProbabilityMeterProps {
  probability: number; // 0-100
}

const ProbabilityMeter: React.FC<ProbabilityMeterProps> = ({ probability }) => {
  const [animatedValue, setAnimatedValue] = useState(0);

  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimatedValue(probability);
    }, 500);

    return () => clearTimeout(timer);
  }, [probability]);

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÈ¢úËâ≤
  const getColor = () => {
    if (probability >= 80) return '#F44336'; // Á∫¢Ëâ≤ - È´òÈ£éÈô©
    if (probability >= 50) return '#FF9800'; // Ê©ôËâ≤ - ‰∏≠È£éÈô©
    return '#4CAF50'; // ÁªøËâ≤ - ‰ΩéÈ£éÈô©
  };

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÈ£éÈô©Á≠âÁ∫ßÊñáÊú¨
  const getRiskLevelText = () => {
    if (probability >= 80) return 'È´òÂ∫¶È£éÈô©';
    if (probability >= 50) return '‰∏≠Â∫¶È£éÈô©';
    return '‰ΩéÂ∫¶È£éÈô©';
  };

  // Ê†πÊçÆÊ¶ÇÁéáÂÄºÁ°ÆÂÆöÊèèËø∞ÊñáÊú¨
  const getDescriptionText = () => {
    if (probability >= 80) {
      return 'ËØ•ËÅå‰∏öÊûÅÊúâÂèØËÉΩË¢´AIÂÆåÂÖ®Ëá™Âä®ÂåñÔºåÂª∫ËÆÆÂ∞ΩÂø´ËΩ¨ÂûãÊàñÊèêÂçáÊäÄËÉΩ„ÄÇ';
    } else if (probability >= 50) {
      return 'ËØ•ËÅå‰∏öÊúâËæÉÂ§ßÂèØËÉΩË¢´ÈÉ®ÂàÜËá™Âä®ÂåñÔºåÈúÄË¶ÅÈÄÇÂ∫îAIËæÖÂä©Â∑•‰ΩúÊ®°Âºè„ÄÇ';
    } else {
      return 'ËØ•ËÅå‰∏öÁü≠ÊúüÂÜÖÂèóAIÂΩ±ÂìçËæÉÂ∞èÔºå‰ΩÜ‰ªçÈúÄÂÖ≥Ê≥®ÊäÄÊúØÂèëÂ±ïË∂ãÂäø„ÄÇ';
    }
  };

  // ËÆ°ÁÆó‰ª™Ë°®ÁõòÊåáÈíàËßíÂ∫¶
  const needleRotation = -90 + (animatedValue / 100) * 180;

  // ËÆ°ÁÆóÂúÜÁéØËøõÂ∫¶
  const circumference = 2 * Math.PI * 70;
  const strokeDashoffset = circumference * (1 - animatedValue / 100);

  return (
    <motion.div 
      className="probability-meter"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8, delay: 0.2 }}
    >
      <h3>AI ÂΩ±ÂìçÁ®ãÂ∫¶</h3>

      <div className="meter-content">
        <div className="circular-gauge-container">
          <div className="circular-gauge">
            <svg viewBox="0 0 200 200">
              {/* ËÉåÊôØÂúÜÁéØ */}
              <circle 
                cx="100" 
                cy="100" 
                r="70" 
                fill="none" 
                stroke="rgba(255, 255, 255, 0.1)" 
                strokeWidth="12"
              />

              {/* ‰ΩéÈ£éÈô©Âå∫Âüü */}
              <circle 
                cx="100" 
                cy="100" 
                r="70" 
                fill="none" 
                stroke="rgba(76, 175, 80, 0.3)" 
                strokeWidth="12"
                strokeDasharray={`${circumference * 0.33} ${circumference * 0.67}`}
                strokeDashoffset="0"
                transform="rotate(-90 100 100)"
              />

              {/* ‰∏≠È£éÈô©Âå∫Âüü */}
              <circle 
                cx="100" 
                cy="100" 
                r="70" 
                fill="none" 
                stroke="rgba(255, 152, 0, 0.3)" 
                strokeWidth="12"
                strokeDasharray={`${circumference * 0.33} ${circumference * 0.67}`}
                strokeDashoffset={`${-circumference * 0.33}`}
                transform="rotate(-90 100 100)"
              />

              {/* È´òÈ£éÈô©Âå∫Âüü */}
              <circle 
                cx="100" 
                cy="100" 
                r="70" 
                fill="none" 
                stroke="rgba(244, 67, 54, 0.3)" 
                strokeWidth="12"
                strokeDasharray={`${circumference * 0.34} ${circumference * 0.66}`}
                strokeDashoffset={`${-circumference * 0.66}`}
                transform="rotate(-90 100 100)"
              />

              {/* ËøõÂ∫¶ÂúÜÁéØ */}
              <circle 
                className="progress-ring"
                cx="100" 
                cy="100" 
                r="70" 
                fill="none" 
                stroke={getColor()} 
                strokeWidth="12"
                strokeDasharray={circumference}
                strokeDashoffset={strokeDashoffset}
                strokeLinecap="round"
                transform="rotate(-90 100 100)"
              />

              {/* ÊåáÈíà */}
              <line 
                x1="100" 
                y1="100" 
                x2="100" 
                y2="40" 
                stroke={getColor()}
                strokeWidth="3"
                strokeLinecap="round"
                transform={`rotate(${needleRotation} 100 100)`}
              />

              {/* ‰∏≠ÂøÉÁÇπ */}
              <circle 
                cx="100" 
                cy="100" 
                r="10" 
                fill={getColor()} 
              />

              {/* Ê¶ÇÁéáÂÄº */}
              <text 
                x="100" 
                y="110" 
                textAnchor="middle" 
                fontSize="24" 
                fontWeight="bold"
                fill="white"
              >
                {animatedValue}%
              </text>
            </svg>

            <div className="gauge-labels">
              <span className="low-label">‰Ωé</span>
              <span className="medium-label">‰∏≠</span>
              <span className="high-label">È´ò</span>
            </div>
          </div>
        </div>

        <motion.div 
          className="risk-card"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.8 }}
        >
          <div className="risk-level" style={{ color: getColor() }}>
            {getRiskLevelText()}
          </div>

          <div className="risk-description">
            {getDescriptionText()}
          </div>
        </motion.div>

        <div className="impact-areas">
          <h4>‰∏ªË¶ÅÂΩ±ÂìçÈ¢ÜÂüü</h4>
          <div className="impact-grid">
            <motion.div 
              className="impact-item"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.3, delay: 1.2 }}
            >
              <div className="impact-icon" style={{ backgroundColor: `${getColor()}20` }}>
                üîÑ
              </div>
              <div className="impact-label">Â∑•‰ΩúÊµÅÁ®ãËá™Âä®Âåñ</div>
              <div className="impact-bar-container">
                <div 
                  className="impact-bar" 
                  style={{ 
                    width: `${Math.min(100, probability + 10)}%`,
                    backgroundColor: getColor()
                  }} 
                />
              </div>
            </motion.div>

            <motion.div 
              className="impact-item"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.3, delay: 1.4 }}
            >
              <div className="impact-icon" style={{ backgroundColor: `${getColor()}20` }}>
                üß†
              </div>
              <div className="impact-label">ÂÜ≥Á≠ñËæÖÂä©</div>
              <div className="impact-bar-container">
                <div 
                  className="impact-bar" 
                  style={{ 
                    width: `${Math.min(100, probability - 5)}%`,
                    backgroundColor: getColor()
                  }} 
                />
              </div>
            </motion.div>

            <motion.div 
              className="impact-item"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
