import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { useJob } from '../contexts/JobContext';
import '../styles/JobImpact.css';

// ÈªòËÆ§ÊäÄËÉΩÂàóË°®ÔºåÂΩìËÅå‰∏öÊ≤°ÊúâÁâπÂÆöÊäÄËÉΩÊó∂‰ΩøÁî®
const defaultSkills = {
  technical: [
    'AI Â∑•ÂÖ∑‰ΩøÁî®‰∏éÈõÜÊàêËÉΩÂäõ',
    'Êï∞ÊçÆÂàÜÊûê‰∏éËß£ËØªËÉΩÂäõ',
    'Êï∞Â≠óÂåñÂ∑•‰ΩúÊµÅÁ®ãÊéåÊè°',
    'Êñ∞ÊäÄÊúØÂø´ÈÄüÂ≠¶‰π†ËÉΩÂäõ'
  ],
  cognitive: [
    'Â§çÊùÇÈóÆÈ¢òËß£ÂÜ≥ËÉΩÂäõ',
    'Á≥ªÁªüÊÄßÊÄùÁª¥ÊñπÂºè',
    'ÂàõÊñ∞‰∏éÂàõÈÄ†ÊÄßÊÄùÁª¥',
    'Ë∑®È¢ÜÂüüÁü•ËØÜÊï¥Âêà'
  ]
};

const formatCountdown = (targetYear: number): string => {
  const now = new Date();
  const target = new Date(targetYear, 0, 1); // January 1st of target year
  const timeDiff = target.getTime() - now.getTime();

  if (timeDiff <= 0) return 'Â∑≤Âà∞Ëææ';

  const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
  const years = Math.floor(days / 365);
  const months = Math.floor((days % 365) / 30);
  const remainingDays = days % 30;
  const hours = Math.floor((timeDiff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
  const minutes = Math.floor((timeDiff % (1000 * 60 * 60)) / (1000 * 60));

  return `${years}Âπ¥ ${months}Êúà ${remainingDays}Â§© ${hours}Êó∂ ${minutes}ÂàÜ`;
};

const JobImpact: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob } = useJob();

  if (!selectedJob) {
    return null;
  }

  // ‰ΩøÁî®ËÅå‰∏öÁâπÂÆöÊäÄËÉΩÊàñÈªòËÆ§ÊäÄËÉΩ
  const skills = selectedJob.skillsNeeded || defaultSkills;

  return (
    <motion.div 
      className="job-impact"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="impact-header">
        <h3>ÊäÄËÉΩÊèêÂçáÊåáÂçó</h3>
        <p className="impact-description">AI Êó∂‰ª£ÂøÖÂ§áÁöÑÊ†∏ÂøÉËÉΩÂäõ‰∏éÂ≠¶‰π†ÊñπÂêë</p>
        <div className="countdown-container">
          <div className="countdown-label">ËÅå‰∏öËá™Âä®ÂåñÂÄíËÆ°Êó∂</div>
          <div className="countdown">{formatCountdown(selectedJob.predictedYear)}</div>
        </div>
      </div>

      <div className="impact-content">
        <div className="impact-details">
          <div className="impact-section technical">
            <div className="section-header">
              <h4>ÊäÄÊúØËÉΩÂäõ</h4>
              <span className="section-icon">üíª</span>
            </div>
            <ul>
              {skills.technical.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>

          <div className="impact-section cognitive">
            <div className="section-header">
              <h4>ËÆ§Áü•ËÉΩÂäõ</h4>
              <span className="section-icon">üß†</span>
            </div>
            <ul>
              {skills.cognitive.map((skill, index) => (
                <li key={index}>{skill}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

export default JobImpact;
