import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { jobsData } from '../data/jobsData';
import { Job } from '../types/types';

interface JobSearchProps {
  onSelectJob: (job: Job) => void;
  initialJob: Job | null;
}

const JobSearch: React.FC<JobSearchProps> = ({ onSelectJob, initialJob }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredJobs, setFilteredJobs] = useState<Job[]>([]);
  const [showResults, setShowResults] = useState(false);
  const searchRef = useRef<HTMLDivElement>(null);

  // ÂàùÂßãÂåñÊêúÁ¥¢Ê°Ü
  useEffect(() => {
    if (initialJob) {
      setSearchTerm(initialJob.title);
    }
  }, [initialJob]);

  // Ê†πÊçÆÊêúÁ¥¢ËØçËøáÊª§ËÅå‰∏ö
  useEffect(() => {
    if (searchTerm.trim() === '') {
      // ÂΩìÊêúÁ¥¢Ê°Ü‰∏∫Á©∫Êó∂ÔºåÊòæÁ§∫ÊâÄÊúâËÅå‰∏ö
      setFilteredJobs(jobsData);
      return;
    }

    const results = jobsData.filter(job =>
      job.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredJobs(results);
  }, [searchTerm]);

  // ÁÇπÂáªÂ§ñÈÉ®ÂÖ≥Èó≠‰∏ãÊãâÊ°Ü
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (searchRef.current && !searchRef.current.contains(event.target as Node)) {
        setShowResults(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleSelectJob = (job: Job) => {
    onSelectJob(job);
    setSearchTerm(job.title);
    setShowResults(false);
  };

  return (
    <div className="job-search" ref={searchRef}>
      <h2>Êü•ËØ¢ÊÇ®ÁöÑËÅå‰∏ö</h2>
      <div className="search-container">
        <div className="search-input-wrapper">
          <input
            type="text"
            className="search-input"
            placeholder="ËæìÂÖ•ËÅå‰∏öÂêçÁß∞..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onFocus={() => {
              // ÂΩìËæìÂÖ•Ê°ÜËé∑ÂæóÁÑ¶ÁÇπÊó∂ÔºåÊòæÁ§∫‰∏ãÊãâÂàóË°®
              setShowResults(true);
              // Â¶ÇÊûúÊêúÁ¥¢Ê°Ü‰∏∫Á©∫ÔºåÊòæÁ§∫ÊâÄÊúâËÅå‰∏ö
              if (searchTerm.trim() === '') {
                setFilteredJobs(jobsData);
              }
            }}
          />
          <div className="search-icon">üîç</div>
          <button 
            className="dropdown-toggle" 
            onClick={() => {
              setShowResults(!showResults);
              if (!showResults) {
                setFilteredJobs(jobsData);
              }
            }}
            aria-label="ÊòæÁ§∫ÊâÄÊúâËÅå‰∏ö"
          >
            {showResults ? '‚ñ≤' : '‚ñº'}
          </button>
        </div>

        <AnimatePresence>
          {showResults && (
            <motion.div
              className="search-results"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <div
                    key={job.id}
                    className="search-result-item"
                    onClick={() => handleSelectJob(job)}
                  >
                    {job.title}
                  </div>
                ))
              ) : (
                <div className="no-results">
                  <p>Êú™ÊâæÂà∞Áõ∏ÂÖ≥ËÅå‰∏öÔºåËØ∑Â∞ùËØïÂÖ∂‰ªñÂÖ≥ÈîÆËØç</p>
                </div>
              )}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default JobSearch;
