import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import JobSearch from './components/JobSearch';
import CountdownClock from './components/CountdownClock';
import ProbabilityMeter from './components/ProbabilityMeter';
import JobIcon3D from './components/JobIcon3D';
import Timeline from './components/Timeline';
import AdaptationStrategies from './components/AdaptationStrategies';
import StarfieldBackground from './components/StarfieldBackground';
import AlertOverlay from './components/AlertOverlay';
import { Job } from './types/types';
import { jobsData } from './data/jobsData';
import { jobsDataEn } from './data/jobsDataEn';
import './App.css';
import JobDetails from './components/JobDetails';
import { LanguageProvider } from './contexts/LanguageContext';
import LanguageSwitcher from './components/LanguageSwitcher';
import { useLanguage } from './contexts/LanguageContext';

const AppContent: React.FC = () => {
  const [selectedJob, setSelectedJob] = useState<Job | null>(null);
  const { t, language } = useLanguage();

  // 根据当前语言选择职业数据
  const currentJobsData = language === 'en' ? jobsDataEn : jobsData;

  // 在组件加载时设置默认职业为"程序员"/"Programmer"
  useEffect(() => {
    const defaultJob = currentJobsData.find(job => job.id === "programmer") || currentJobsData[0];
    setSelectedJob(defaultJob);
  }, [language, currentJobsData]);

  return (
    <div className="app">
      <header>
        <LanguageSwitcher />
        <h1>{t('app.title')}</h1>
        <p>{t('app.subtitle')}</p>
      </header>
      <main>
        <JobSearch onSelectJob={setSelectedJob} initialJob={selectedJob} />
        {selectedJob && <JobDetails selectedJob={selectedJob} />}
      </main>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <LanguageProvider>
      <AppContent />
    </LanguageProvider>
  );
};

export default App;
