import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Job } from '../types/types';
import { jobsData } from '../data/jobsData';
import { jobsDataEn } from '../data/jobsDataEn';
import { useLanguage } from '../contexts/LanguageContext';

interface JobSearchProps {
  onSelectJob: (job: Job) => void;
  initialJob: Job | null;
}

const JobSearch: React.FC<JobSearchProps> = ({ onSelectJob, initialJob }) => {
  const { t, language } = useLanguage();
  const [searchTerm, setSearchTerm] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [filteredJobs, setFilteredJobs] = useState<Job[]>([]);
  const dropdownRef = useRef<HTMLDivElement>(null);

  // Ê†πÊçÆÂΩìÂâçËØ≠Ë®ÄÈÄâÊã©ËÅå‰∏öÊï∞ÊçÆ
  const currentJobsData = language === 'en' ? jobsDataEn : jobsData;

  // ÂΩìËØ≠Ë®ÄÂèòÂåñÊó∂ÔºåÊõ¥Êñ∞ÈÄâ‰∏≠ÁöÑËÅå‰∏ö
  useEffect(() => {
    if (initialJob) {
      const jobId = initialJob.id;
      const newJob = currentJobsData.find(job => job.id === jobId);
      if (newJob) {
        onSelectJob(newJob);
      }
    }
  }, [language, initialJob, onSelectJob, currentJobsData]);

  // Â§ÑÁêÜÊêúÁ¥¢ËæìÂÖ•ÂèòÂåñ
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value.trim() === '') {
      setFilteredJobs([]);
    } else {
      const filtered = currentJobsData.filter(job => 
        job.title.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredJobs(filtered);
    }

    setIsDropdownOpen(true);
  };

  // Â§ÑÁêÜËÅå‰∏öÈÄâÊã©
  const handleJobSelect = (job: Job) => {
    setSearchTerm(job.title);
    onSelectJob(job);
    setIsDropdownOpen(false);
  };

  // Â§ÑÁêÜ‰∏ãÊãâËèúÂçïÂàáÊç¢
  const toggleDropdown = () => {
    if (isDropdownOpen) {
      setIsDropdownOpen(false);
    } else {
      setFilteredJobs(currentJobsData);
      setIsDropdownOpen(true);
    }
  };

  // ÁÇπÂáªÂ§ñÈÉ®ÂÖ≥Èó≠‰∏ãÊãâËèúÂçï
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // ËÆæÁΩÆÂàùÂßãËÅå‰∏öÂêçÁß∞
  useEffect(() => {
    if (initialJob) {
      setSearchTerm(initialJob.title);
    }
  }, [initialJob]);

  return (
    <motion.div 
      className="job-search"
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2>{t('search.title')}</h2>
      <div className="search-container" ref={dropdownRef}>
        <div className="search-input-wrapper">
          <span className="search-icon">üîç</span>
          <input
            type="text"
            className="search-input"
            placeholder={t('search.placeholder')}
            value={searchTerm}
            onChange={handleSearchChange}
            onClick={() => setIsDropdownOpen(true)}
          />
          <button className="dropdown-toggle" onClick={toggleDropdown}>
            {isDropdownOpen ? '‚ñ≤' : '‚ñº'}
          </button>
        </div>

        <AnimatePresence>
          {isDropdownOpen && (
            <motion.ul
              className="dropdown-list"
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
            >
              {filteredJobs.length > 0 ? (
                filteredJobs.map(job => (
                  <li 
                    key={job.id} 
                    className="dropdown-item"
                    onClick={() => handleJobSelect(job)}
                  >
                    {job.title}
                  </li>
                ))
              ) : (
                <li className="dropdown-item">{t('search.noResults')}</li>
              )}
            </motion.ul>
          )}
        </AnimatePresence>
      </div>
    </motion.div>
  );
};

export default JobSearch;
