import React, { useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { Box, Sphere, Torus } from '@react-three/drei';
import * as THREE from 'three';

interface JobIcon3DProps {
  jobId: string;
  riskLevel: 'low' | 'medium' | 'high';
}

const getIconGeometry = (jobId: string) => {
  switch (jobId) {
    case 'programmer':
      return <Box args={[1.5, 1.5, 1.5]} />;
    case 'doctor':
      return <Sphere args={[1, 32, 32]} />;
    case 'teacher':
      return <Box args={[1.2, 1.8, 0.8]} />;
    case 'driver':
      return <Torus args={[1, 0.4, 16, 32]} />;
    case 'accountant':
      return <Box args={[1.2, 1.2, 1.2]} />;
    default:
      return <Box args={[1, 1, 1]} />;
  }
};

const getIconColor = (riskLevel: 'low' | 'medium' | 'high') => {
  switch (riskLevel) {
    case 'low':
      return '#4CAF50'; // 绿色
    case 'medium':
      return '#FF9800'; // 橙色
    case 'high':
      return '#F44336'; // 红色
    default:
      return '#2196F3'; // 蓝色
  }
};

const RotatingIcon: React.FC<JobIcon3DProps> = ({ jobId, riskLevel }) => {
  const meshRef = useRef<THREE.Mesh>(null);

  useFrame((state) => {
    if (meshRef.current) {
      meshRef.current.rotation.y += 0.01;
      meshRef.current.rotation.x = Math.sin(state.clock.getElapsedTime() * 0.5) * 0.2;
    }
  });

  const iconColor = getIconColor(riskLevel);
  const IconComponent = getIconGeometry(jobId);

  return (
    <mesh ref={meshRef}>
      {IconComponent}
      <meshStandardMaterial color={iconColor} />
    </mesh>
  );
};

const JobIcon3D: React.FC<JobIcon3DProps> = ({ jobId, riskLevel }) => {
  return (
    <div className="job-icon-3d">
      <h3>职业风险等级: {riskLevel === 'low' ? '低' : riskLevel === 'medium' ? '中' : '高'}</h3>
      <div className="icon-container" style={{ height: '200px' }}>
        <Canvas camera={{ position: [0, 0, 5] }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} intensity={1} />
          <RotatingIcon jobId={jobId} riskLevel={riskLevel} />
        </Canvas>
      </div>
    </div>
  );
};

export default JobIcon3D;
