import React from 'react';
import { motion } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import { useJob } from '../contexts/JobContext';

const JobImpact: React.FC = () => {
  const { t } = useLanguage();
  const { selectedJob } = useJob();

  return (
    <motion.div 
      className="job-impact"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h3>{t('impact.title')}</h3>
      <p className="impact-description">{t('impact.description')}</p>

      <div className="impact-sections">
        <div className="impact-section">
          <h4>{t('impact.skills')}</h4>
          <ul>
            <li>• AI 和自动化工具的使用能力</li>
            <li>• 跨学科知识整合</li>
            <li>• 创新思维和问题解决</li>
            <li>• 人机协作能力</li>
          </ul>
        </div>

        <div className="impact-section">
          <h4>{t('impact.automation')}</h4>
          <ul>
            <li>• 自动化概率: {selectedJob?.automationProbability * 100}%</li>
            <li>• 预计时间: {selectedJob?.predictedYear}</li>
            <li>• 风险等级: {t(`risk.${selectedJob?.riskLevel}`)}</li>
          </ul>
        </div>

        <div className="impact-section">
          <h4>{t('impact.market')}</h4>
          <ul>
            <li>• 传统岗位需求下降</li>
            <li>• AI 辅助岗位需求上升</li>
            <li>• 新型混合岗位出现</li>
            <li>• 技能要求持续提升</li>
          </ul>
        </div>
      </div>
    </motion.div>
  );
};

export default JobImpact;
